import { get } from "./API.js";

export function getVenues() {
    const [redraw, forceRedraw] = React.useState(0);

    React.useEffect(()=>{
        getConstants()
    }, [redraw]);

    // this function requests the redraw, and can be passed as an argument to other functions
    const requestRedraw = () => {
        forceRedraw(redraw+1)
    }

    // this sends the ACTUAL POST and retrieves the answer.
    get('/constants')
        .then(function (response) {
            let str = '';
            for (let c of response.constants) {
                str += c.name + '=' + c.value + '<br>';
            }

            // insert HTML in the <div> with 
            // constant-list
            let cd = document.getElementById('constant-list')
            cd.innerHTML = str

        }).catch(function (error) {
            // not much to do
            console.log(error)
        })
}
